package MyNumberProject;

public class NumberFun {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for (int i=1; i<=100; i++){
			//If the number is even,print the number and say it is an even number
			if (i%2==0){
					System.out.println(i+" is an even number");
			
			}
			
			//If not even, just print the number
			else {
			System.out.println(i);
			
		}
		}

	}

}
